package Basico;

public class Variables {

	public static void main(String[] args) {
		/* TODO Auto-generated method stub
		 *Variables para enteros
		 * Dependen de la longitud del número
		*/
		
		byte entero=12;
		System.out.print("Número entero: "+entero);
		
		short entero1=12456;
		System.out.print("Número entero: "+entero1);

		int entero2=1245656;
		System.out.print("Número entero: "+entero2);
		
		long entero3=124565678;
		System.out.print("Número entero: "+entero3);

		//Variables con decimales
		
		float decimal=3.45f;
		System.out.print("Número decimal: "+decimal);
	}

}
